import { TextField } from 'payload'
import { IconPickerArgs } from './types'

export const iconField = (options?: IconPickerArgs): TextField => {
  const baseField: TextField = {
    name: options?.name || 'icon',
    type: 'text',
    required: options?.required || false,
    admin: {
      description: options?.description,
      components: {
        Field: {
          path: '@/fields/lucide-icon-picker',
          clientProps: {
            showTextInput: !!options?.showTextInput, // default false
            searchable: options?.searchable !== false, // default true
            categorized: options?.categorized !== false, // default true
            searchPlaceholder: options?.searchPlaceholder || 'Search icons...',
            triggerPlaceholder: options?.triggerPlaceholder || 'Select Icon',
          },
        },
        Cell: {
          path: '@/fields/lucide-icon-picker#IconCell',
          clientProps: {},
        },
      },
    },
  }
  if (typeof options?.overrides === 'function') {
    return options.overrides(baseField)
  }

  return baseField
}

'use client'
import {
  AnimateHeight,
  Button,
  ChevronIcon,
  Drawer,
  FieldDescription,
  FieldError,
  FieldLabel,
  TextInput,
  useField,
  useModal,
  useTranslation,
} from '@payloadcms/ui'
import { DefaultCellComponentProps, TextFieldClient, TextFieldClientProps } from 'payload'
import React, { useCallback, useEffect, useMemo, useRef, useState } from 'react'
import './style.scss'
import { IconData, IconPickerProps } from './types'
import { DynamicIcon, IconName } from 'lucide-react/dynamic'
import { iconsData } from './icons'
import { useVirtualizer } from '@tanstack/react-virtual'
import Fuse from 'fuse.js'
import { useDebounceValue } from 'usehooks-ts'

interface TooltipProps {
  content: string
  children: React.ReactNode
}

const Tooltip: React.FC<TooltipProps> = React.memo(({ content, children }) => {
  return (
    <div className="icon-picker__tooltip-wrapper">
      {children}
      <div className="icon-picker__tooltip">{content}</div>
    </div>
  )
})
Tooltip.displayName = 'Tooltip'

// Loading Skeleton Component
const IconsGridSkeleton: React.FC = () => {
  return (
    <div className="icon-picker__skeleton">
      <div className="icon-picker__skeleton-category" />
      <div className="icon-picker__skeleton-grid">
        {Array.from({ length: 40 }).map((_, i) => (
          <div key={i} className="icon-picker__skeleton-item" />
        ))}
      </div>
    </div>
  )
}

// Icon Renderer Component
interface IconRendererProps {
  name: string
  size?: number
}

const IconRenderer: React.FC<IconRendererProps> = React.memo(({ name, size = 20 }) => {
  return <DynamicIcon name={name as IconName} size={size} />
})
IconRenderer.displayName = 'IconRenderer'

// Icon Button Component
interface IconButtonProps {
  icon: IconData
  isSelected: boolean
  onSelect: (iconName: string) => void
  disabled?: boolean
}

const IconButton: React.FC<IconButtonProps> = React.memo(
  ({ icon, isSelected, onSelect, disabled }) => {
    return (
      <Tooltip content={icon.name}>
        <button
          type="button"
          className={
            isSelected
              ? 'icon-picker__icon-button icon-picker__icon-button--selected'
              : 'icon-picker__icon-button'
          }
          onClick={() => onSelect(icon.name)}
          disabled={disabled}
          aria-label={`Select ${icon.name} icon`}
        >
          <IconRenderer name={icon.name} size={24} />
        </button>
      </Tooltip>
    )
  },
  (prevProps, nextProps) => {
    return (
      prevProps.icon.name === nextProps.icon.name &&
      prevProps.isSelected === nextProps.isSelected &&
      prevProps.disabled === nextProps.disabled
    )
  },
)
IconButton.displayName = 'IconButton'

const LucideIconPicker: React.FC<TextFieldClientProps & IconPickerProps> = ({
  path,
  field,
  showTextInput = true,
  searchable = true,
  categorized = true,
  searchPlaceholder = 'Search icons...',
  triggerPlaceholder = 'Select Icon',
  readOnly,
}) => {
  const { setValue, value, disabled, showError } = useField<string>({ path: path || field.name })
  const actualReadOnly = readOnly || field.admin?.readOnly || false
  const { openModal, closeModal } = useModal()

  const [searchInput, setSearchInput] = useState('')
  const [debouncedSearch] = useDebounceValue(searchInput, 200)
  const [isDrawerOpen, setIsDrawerOpen] = useState(false)
  const [isLoading, setIsLoading] = useState(true)
  const [showCategories, setShowCategories] = useState(false)

  const drawerSlug = `icon-${field.name}-drawer-${path}`

  // Fuse.js instance for fuzzy search
  const fuseInstance = useMemo(() => {
    return new Fuse(iconsData, {
      keys: ['name', 'tags', 'categories'],
      threshold: 0.3,
      ignoreLocation: true,
      includeScore: true,
    })
  }, [])

  // Filtered icons based on search
  const filteredIcons = useMemo(() => {
    if (debouncedSearch.trim() === '') {
      return iconsData
    }

    const results = fuseInstance.search(debouncedSearch.toLowerCase().trim())
    return results.map((result) => result.item)
  }, [debouncedSearch, fuseInstance])

  // Categorized icons
  const categorizedIcons = useMemo(() => {
    if (!categorized || debouncedSearch.trim() !== '') {
      return [{ name: 'All Icons', icons: filteredIcons }]
    }

    const categories = new Map<string, IconData[]>()

    filteredIcons.forEach((icon) => {
      if (icon.categories && icon.categories.length > 0) {
        icon.categories.forEach((category) => {
          if (!categories.has(category)) {
            categories.set(category, [])
          }
          categories.get(category)!.push(icon)
        })
      } else {
        const category = 'Other'
        if (!categories.has(category)) {
          categories.set(category, [])
        }
        categories.get(category)!.push(icon)
      }
    })

    return Array.from(categories.entries())
      .map(([name, icons]) => ({ name, icons }))
      .sort((a, b) => a.name.localeCompare(b.name))
  }, [filteredIcons, categorized, debouncedSearch])

  const parentRef = useRef<HTMLDivElement>(null)
  const [containerWidth, setContainerWidth] = useState(0)

  // Calculate container width and icons per row
  useEffect(() => {
    if (!parentRef.current) return

    const updateWidth = () => {
      if (parentRef.current) {
        setContainerWidth(parentRef.current.offsetWidth)
      }
    }

    updateWidth()
    const resizeObserver = new ResizeObserver(updateWidth)
    resizeObserver.observe(parentRef.current)

    return () => resizeObserver.disconnect()
  }, [isDrawerOpen])

  // Calculate icons per row based on container width
  const iconsPerRow = useMemo(() => {
    if (containerWidth === 0) return 8
    // Icon: 70-85px, gap: 10px
    const iconWidth = 85
    const gap = 10
    const calculated = Math.floor((containerWidth + gap) / (iconWidth + gap))
    return Math.max(4, calculated)
  }, [containerWidth])

  // Flatten icons into rows with category headers
  const gridRows = useMemo(() => {
    const rows: Array<
      | { type: 'category'; name: string; index: number }
      | { type: 'iconRow'; icons: IconData[]; index: number }
    > = []
    let rowIndex = 0

    categorizedIcons.forEach((category) => {
      // Add category header
      rows.push({ type: 'category', name: category.name, index: rowIndex++ })

      // Split icons into rows
      for (let i = 0; i < category.icons.length; i += iconsPerRow) {
        const rowIcons = category.icons.slice(i, i + iconsPerRow)
        rows.push({ type: 'iconRow', icons: rowIcons, index: rowIndex++ })
      }
    })

    return rows
  }, [categorizedIcons, iconsPerRow])

  // Row virtualizer
  const rowVirtualizer = useVirtualizer({
    count: gridRows.length,
    getScrollElement: () => parentRef.current,
    estimateSize: (index) => {
      const row = gridRows[index]
      return row?.type === 'category' ? 50 : 100
    },
    overscan: 5,
  })

  const handleIconSelect = useCallback(
    (iconName: string) => {
      if (value === iconName) {
        setValue(null)
        closeModal(drawerSlug)
        setSearchInput('')
        return
      }
      setValue(iconName)
      closeModal(drawerSlug)
      setSearchInput('')
    },
    [setValue, closeModal, drawerSlug, value],
  )

  const handleTextInputChange = useCallback(
    (event: React.ChangeEvent<HTMLInputElement>) => {
      const iconName = event?.target.value
      setValue(iconName)
    },
    [setValue],
  )

  const handleSearchChange = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      setSearchInput(e.target.value)
      rowVirtualizer.scrollToOffset(0)
    },
    [rowVirtualizer],
  )

  const scrollToCategory = useCallback(
    (categoryName: string) => {
      const categoryIndex = gridRows.findIndex(
        (row) => row.type === 'category' && row.name === categoryName,
      )
      if (categoryIndex !== -1) {
        rowVirtualizer.scrollToIndex(categoryIndex, { align: 'start' })
      }
    },
    [gridRows, rowVirtualizer],
  )

  // Handle drawer open/close
  useEffect(() => {
    if (isDrawerOpen) {
      setIsLoading(true)
      const timer = setTimeout(() => {
        setIsLoading(false)
        requestAnimationFrame(() => {
          rowVirtualizer.measure()
        })
      }, 50)

      return () => clearTimeout(timer)
    }
  }, [isDrawerOpen, rowVirtualizer])

  const isDisabled = actualReadOnly || disabled

  const virtualRows = rowVirtualizer.getVirtualItems()

  // Virtualized content
  const virtualContent = (
    <>
      {filteredIcons.length === 0 ? (
        <div className="icon-picker__no-results">No icons found</div>
      ) : (
        <div
          style={{
            height: `${rowVirtualizer.getTotalSize()}px`,
            width: '100%',
            position: 'relative',
          }}
        >
          {virtualRows.map((virtualRow) => {
            const row = gridRows[virtualRow.index]
            if (!row) return null

            return (
              <div
                key={virtualRow.key}
                data-index={virtualRow.index}
                ref={rowVirtualizer.measureElement}
                style={{
                  position: 'absolute',
                  top: 0,
                  left: 0,
                  width: '100%',
                  transform: `translateY(${virtualRow.start}px)`,
                }}
              >
                {row.type === 'category' ? (
                  <div className="icon-picker__category-header">
                    <h3 className="icon-picker__category-title">{row.name}</h3>
                    <div className="icon-picker__category-divider" />
                  </div>
                ) : (
                  <div className="icon-picker__icon-row">
                    {row.icons.map((icon) => (
                      <div key={icon.name} className="icon-picker__icon-cell">
                        <IconButton
                          icon={icon}
                          isSelected={value === icon.name}
                          onSelect={handleIconSelect}
                          disabled={isDisabled}
                        />
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )
          })}
        </div>
      )}
    </>
  )

  return (
    <div className="icon-picker">
      <FieldLabel
        label={field.label}
        localized={field.localized}
        path={path}
        required={field.required}
      />
      <div className="icon-picker__main">
        <div className="icon-picker__controls">
          <Button
            onClick={() => {
              if (!actualReadOnly && !disabled) {
                setIsDrawerOpen(true)
                openModal(drawerSlug)
              }
            }}
            disabled={actualReadOnly || disabled}
            margin={false}
            buttonStyle={'secondary'}
            className={showError ? 'icon-picker__error-btn' : undefined}
          >
            {value ? (
              <span className="icon-picker__selected-icon">
                <IconRenderer name={value} size={18} />
                <span>{value}</span>
              </span>
            ) : (
              triggerPlaceholder
            )}
          </Button>
          {value && !actualReadOnly && !disabled && (
            <Button
              onClick={() => setValue(null)}
              buttonStyle="icon-label"
              disabled={actualReadOnly || disabled}
              aria-label="Clear selected icon"
              margin={false}
              iconStyle="with-border"
              icon={['x']}
            ></Button>
          )}
          {showTextInput && (
            <TextInput
              value={value || ''}
              onChange={handleTextInputChange}
              placeholder="e.g., home"
              readOnly={actualReadOnly || disabled}
              path={path}
              required={field.required}
            />
          )}
        </div>
        <FieldError showError={showError} path={path} />
      </div>
      <FieldDescription description={field.admin?.description} path={path} />

      <Drawer slug={drawerSlug} title="Select Icon" className="icon-picker__drawer">
        <div className="icon-picker__drawer-header">
          {searchable && (
            <TextInput
              placeholder={searchPlaceholder}
              onChange={handleSearchChange}
              className="icon-picker__search-input"
              value={searchInput}
              path={`${path}-search`}
            />
          )}
          {categorized && debouncedSearch.trim() === '' && (
            <>
              <Button
                type="button"
                onClick={() => setShowCategories(!showCategories)}
                icon={<ChevronIcon direction={showCategories ? 'up' : 'down'} />}
                margin={false}
              >
                <span>Browse by category</span>
              </Button>
              <AnimateHeight height={showCategories ? 'auto' : 0}>
                <div className="icon-picker__categories">
                  {categorizedIcons.map((category) => (
                    <Button
                      key={category.name}
                      type="button"
                      margin={false}
                      size="xsmall"
                      buttonStyle="pill"
                      onClick={() => scrollToCategory(category.name)}
                    >
                      {category.name.charAt(0).toUpperCase() + category.name.slice(1)}
                    </Button>
                  ))}
                </div>
              </AnimateHeight>
            </>
          )}
        </div>
        <div ref={parentRef} className="icon-picker__drawer-content">
          {isLoading ? <IconsGridSkeleton /> : virtualContent}
        </div>
      </Drawer>
    </div>
  )
}

export const IconCell: React.FC<DefaultCellComponentProps<TextFieldClient>> = (props) => {
  const { cellData, field } = props
  const { i18n } = useTranslation()

  return (
    <div className="icon-cell">
      {cellData && (
        <div className="icon-cell__preview">
          <IconRenderer name={cellData} size={16} />
        </div>
      )}
      <span>
        {cellData
          ? cellData
          : i18n.t('general:noLabel', {
              label: field.name,
            })}
      </span>
    </div>
  )
}

export default LucideIconPicker

.icon-picker {
  display: flex;
  flex-direction: column;
  margin-bottom: 1.5rem;

  &__main {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
  }

  &__controls {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    flex-wrap: wrap;
  }

  &__selected-icon {
    display: flex;
    align-items: center;
    gap: 0.5rem;
  }

  &__error-btn {
    --box-shadow: inset 0 0 0 1px var(--color-error-500) !important;
  }

  &__drawer {
    .drawer__content {
      max-width: 750px;
      max-height: 100vh;
    }
    .drawer__content-children {
      display: flex;
      flex-direction: column;
      height: 100%;
    }
  }

  &__drawer-header {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    padding: 1rem;
    margin-bottom: 1rem;
    border-bottom: 1px solid var(--theme-elevation-150, #e0e0e0);
    background: var(--theme-elevation-0, #fff);
    position: sticky;
    top: 0;
    z-index: 10;
  }

  &__categories {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
    overflow-x: auto;
    padding-bottom: 0.5rem;

    &::-webkit-scrollbar {
      height: 6px;
    }

    &::-webkit-scrollbar-track {
      background: var(--theme-elevation-100, #f5f5f5);
      border-radius: 3px;
    }

    &::-webkit-scrollbar-thumb {
      background: var(--theme-elevation-300, #ccc);
      border-radius: 3px;

      &:hover {
        background: var(--theme-elevation-400, #999);
      }
    }
  }

  &__drawer-content {
    padding: 0 1rem 1rem 1rem;
    overflow-y: auto;
    overflow-x: hidden;
    position: relative;
    flex-grow: 1;

    &::-webkit-scrollbar {
      width: 8px;
    }

    &::-webkit-scrollbar-track {
      background: var(--theme-elevation-100, #f5f5f5);
    }

    &::-webkit-scrollbar-thumb {
      background: var(--theme-elevation-300, #ccc);
      border-radius: 4px;

      &:hover {
        background: var(--theme-elevation-400, #999);
      }
    }
  }

  &__category-header {
    width: 100%;
    padding: 0 0 0.5rem;
    margin-bottom: 0.75rem;
  }

  &__category-title {
    font-size: 0.875rem;
    font-weight: 600;
    color: var(--theme-elevation-700, #555);
    margin: 0 0 0.25rem 0;
    text-transform: capitalize;
  }

  &__category-divider {
    height: 1px;
    background: var(--theme-elevation-150, #e0e0e0);
  }

  &__icon-row {
    display: flex;
    flex-wrap: nowrap;
    gap: 0.625rem;
    width: 100%;
    padding: 0.5rem 0;
  }

  &__icon-cell {
    flex: 0 0 auto;
    width: 85px;
    min-width: 70px;
  }

  &__icon-button {
    width: 100%;
    aspect-ratio: 1 / 1;
    padding: 0.75rem;
    border: 2px solid var(--theme-elevation-150, #e0e0e0);
    border-radius: var(--style-radius-s, 4px);
    background: var(--theme-elevation-0, #fff);
    cursor: pointer;
    transition: all 0.2s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--theme-elevation-800, #333);

    svg {
      flex-shrink: 0;
      width: 24px;
      height: 24px;
    }

    &:hover:not(:disabled) {
      background: var(--theme-elevation-50, #fafafa);
      border-color: var(--theme-elevation-400, #999);
      transform: translateY(-2px);
      box-shadow: 0 4px 8px var(--theme-elevation-200, rgba(0, 0, 0, 0.1));
    }

    &:active:not(:disabled) {
      transform: translateY(0);
    }

    &:focus {
      outline: none;
      border-color: var(--theme-elevation-500, #4a90e2);
      box-shadow: 0 0 0 3px var(--theme-elevation-100, rgba(74, 144, 226, 0.2));
    }

    &:disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    &--selected {
      border-color: var(--theme-elevation-500, #4a90e2);
      border-width: 3px;
      background: var(--theme-elevation-50, rgba(74, 144, 226, 0.05));
      box-shadow: 0 0 0 3px var(--theme-elevation-100, rgba(74, 144, 226, 0.2));
    }
  }

  &__tooltip-wrapper {
    position: relative;
    display: block;
    width: 100%;
    z-index: 1;

    &:hover {
      z-index: 100;

      .icon-picker__tooltip {
        opacity: 1;
        visibility: visible;
      }
    }
  }

  &__tooltip {
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%) translateY(-8px);
    padding: 0.375rem 0.75rem;
    background: var(--theme-elevation-900, #1a1a1a);
    color: var(--theme-elevation-0, #fff);
    font-size: 0.75rem;
    border-radius: var(--style-radius-s, 4px);
    white-space: nowrap;
    opacity: 0;
    visibility: hidden;
    transition: all 0.2s ease;
    pointer-events: none;
    z-index: 9999;
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);

    &::after {
      content: '';
      position: absolute;
      top: 100%;
      left: 50%;
      transform: translateX(-50%);
      border: 4px solid transparent;
      border-top-color: var(--theme-elevation-900, #1a1a1a);
    }
  }

  &__no-results {
    text-align: center;
    padding: 2rem;
    color: var(--theme-elevation-500, #888);
    font-size: 0.875rem;
  }

  &__skeleton {
    display: flex;
    flex-direction: column;
    gap: 1rem;
  }

  &__skeleton-category {
    height: 20px;
    width: 30%;
    background: linear-gradient(
      90deg,
      var(--theme-elevation-100, #f5f5f5) 25%,
      var(--theme-elevation-150, #e0e0e0) 50%,
      var(--theme-elevation-100, #f5f5f5) 75%
    );
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s ease-in-out infinite;
    border-radius: var(--style-radius-s, 4px);
  }

  &__skeleton-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(70px, 85px));
    gap: 0.625rem;
  }

  &__skeleton-item {
    aspect-ratio: 1;
    background: linear-gradient(
      90deg,
      var(--theme-elevation-100, #f5f5f5) 25%,
      var(--theme-elevation-150, #e0e0e0) 50%,
      var(--theme-elevation-100, #f5f5f5) 75%
    );
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s ease-in-out infinite;
    border-radius: var(--style-radius-s, 4px);
  }
}

@keyframes skeleton-loading {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

.icon-cell {
  display: flex;
  align-items: center;
  gap: 0.5rem;

  &__preview {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 24px;
    height: 24px;
    border-radius: var(--style-radius-s, 4px);
    border: 1px solid var(--theme-elevation-150, #e0e0e0);
    background: var(--theme-elevation-50, #fafafa);
    color: var(--theme-elevation-800, #333);
  }
}

@media (max-width: 768px) {
  .icon-picker {
    &__drawer {
      .drawer__content {
        max-width: 100vw; // Use full width on mobile
      }
    }

    &__icon-cell {
      width: 75px;
      min-width: 60px;
    }

    &__icon-row {
      gap: 0.5rem; // Smaller gap on tablets
    }
  }
}

@media (max-width: 480px) {
  .icon-picker {
    &__drawer {
      .drawer__content {
        max-width: 100vw;
        padding: 0; // Remove drawer padding on small screens
      }
    }

    &__icon-cell {
      width: 65px;
      min-width: 50px;
    }

    &__icon-row {
      gap: 0.375rem; // Even smaller gap on mobile
    }

    &__drawer-content {
      padding: 0 0.5rem 0.5rem; // Reduce side padding
    }

    &__drawer-header {
      padding: 0.5rem;
    }
  }
}

@media (max-width: 380px) {
  .icon-picker {
    &__icon-cell {
      width: 55px;
      min-width: 45px;
    }

    &__icon-row {
      gap: 0.25rem; // Minimal gap for very small screens
    }

    &__icon-button {
      padding: 0.375rem;

      svg {
        width: 16px;
        height: 16px;
      }
    }
  }
}
